import logo from '../imgs/logo2.png';
import {useEffect, useState} from "react"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome} from '@fortawesome/free-solid-svg-icons';
import {Nav, Navbar, Col, Row} from 'react-bootstrap';
import {Link, useNavigate} from "react-router-dom";
import "./NavigationBar.css"


const NavigationBar = () => {
    const [checkLoggedIn, setCheckLoggedIn] = useState(false)
    var loggedIn;
    // var loggedIn;
    const navigate = useNavigate();

    useEffect(() => {
        console.log("Print 1: " + localStorage.getItem("loggedIn"));
        setCheckLoggedIn(localStorage.getItem("loggedIn"))
        // loggedIn = localStorage.getItem("loggedIn")
        console.log("Print 2: " + checkLoggedIn);
      }, [localStorage.getItem("loggedIn")]);

      const logout =()=>{
        localStorage.setItem("loggedIn", false)
        setCheckLoggedIn(false);
        localStorage.removeItem("username");
        navigate("/");
      }

      const checkStatus = () => {
          console.log("Printing storage: " + localStorage.getItem("loggedIn"))
          console.log("Printing local: " + checkLoggedIn)
      }

    return ( 
        <div>
                <Row>
                    <Col md={12}>
                            <Navbar variant="dark" expand="lg" >
                                <Link to="/">
                                    <Navbar.Brand href="#home">
                                        <div className='logo'>
                                        <img src={logo}></img>
                                        </div>
                                    </Navbar.Brand>
                                </Link>
                                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                                    <Navbar.Collapse id="basic-navbar-nav">
                                        <Nav className="mr-auto">
                                            <Link to="/">
                                                <Nav.Link href="/home">
                                                    <FontAwesomeIcon icon={faHome} size="lg"/>
                                                </Nav.Link>
                                            </Link>
                                            <Link to="/excursions">
                                                <Nav.Link href="/excursions">Excursions</Nav.Link>
                                            </Link>
                                            {/* {console.log("Printing here: " + checkLoggedIn)} */}
                                            { checkLoggedIn ? (

                                                <>
                                                {/* {console.log("Printing here2: " + checkLoggedIn)} */}
                                                    <Link to="/customerDash">
                                                        <Nav.Link href="/dashboard">Dashboard</Nav.Link>
                                                    </Link>
                                                    <Nav.Link onClick={()=>logout()}>Logout</Nav.Link>
                                                </>

                                             ) : (
                                                <>
                                                <Link to="/login">
                                                    <Nav.Link href="/login">Login</Nav.Link>
                                                 </Link>
                                                <Link to="/register">
                                                    <Nav.Link href="/register">Register</Nav.Link>
                                                </Link>
                                                </>
                                             )}
                                           <button onMouseOver={()=>checkStatus()}>hover</button>
                                            {/* <Link to="/cart">
                                                <Nav.Link href="/cart">
                                                    <FontAwesomeIcon icon={faShoppingCart} size="lg"/>
                                                </Nav.Link>
                                            </Link> */}
                                        </Nav>
                                    </Navbar.Collapse>
                            </Navbar>
                            <br />
                    </Col>   
               </Row>
        </div>

    )

}

export default NavigationBar
