import Form from 'react-bootstrap/Form'
// import "./styling/CartExcursionsCSS.css"
// import Button from "../../components/Button.js";
import { useNavigate } from "react-router-dom"
import "./Cart.css"
import { useState, useEffect } from 'react'
import Axios from "axios"
import { excursionDetails } from '../../components/excursionDetails'



const Cart = () => {

    const navigate = useNavigate();
    const [excursionInfo, setExcursionInfo] = useState([]);

    const name = "2 Hour Guided Hike"
    const index = 0;

    useEffect(() => {
        Axios.get(
            `http://localhost:3001/excursionInfo/${name}`
        ).then((response) => {
            setExcursionInfo(response.data);
        });
    });

    const [timeSlots, setTimeSlots] = useState([]);

    useEffect(() => {
        Axios.get(
            `http://localhost:3001/timeSlots/${name}`)
            .then((response) => {
                setTimeSlots(response.data);
            });
    }, []);


    var costPerPerson;

    excursionInfo.map((val) => {
        costPerPerson = val.Cost;
    })




    // const [Participants, setNumParticipants] = useState(0);
    // const [AgreementSignature, setAgreementSignature] = useState("")


    // const BookingID = "TestID13"
    // const NumAdults = 3;                //change in DB?
    // const NumMinors = 0;
    // const Cost = total;
    // const TimeSlot = "HIKE1"
    // const ExcursionName = "2 Hour Guided Hike";
    // const Participants = 3;
    // const Receipt = "I will pay onsite"
