import Form from 'react-bootstrap/Form'
import "./styling/CartExcursionsCSS.css"
import CartExcursions from "./CartExcursions";
import Button from "./Button.js";
import {Link} from "react-router-dom"
import "./styling/CartCSS.css"

// const enableCompletion =(linkID)=>{
//     document.getElementById(linkID).disabled = "false";
// }


const Cart = ({excursionList, excursionDescriptions, excursionIndex, slots}) => {
    var total = 0;

    const updateSubtotal =(total, num)=> {
        // return total = num * excursionList[excursionIndex].Cost;
    };

    console.log(total)
    return (  
        <div className='cartBlock'>
            <div className='cartHeader'>
                <h2>Cart</h2>
                {/* <h3>{booking[bookingIndex].bookingFName} {booking[bookingIndex].bookingLName}</h3> */}
            </div>
            <div className='cartExcursionDetails'>
                    <div className='cartExcursionImg'>
                        <img src={excursionDescriptions.imgURL}/>
                    </div>
                    <div className='cartExcursionDetailsText'>
                    <div className='cartExcursionDetailsTitle'>
                        {/* <h3>{excursionList[excursionIndex].title}</h3> */}
                    </div>
                    <div className='cartExcursionDetailsDate'>
                    <Form>
                        <Form.Group className="mb-3" controlId="formBasicEmail">
                            <Form.Label>Booking Date: </Form.Label>
                            <Form.Select type="email" placeholder="Email Address" required>
                                 <option>Select A Date</option>
                                 {slots.map((slots => <option value={slots.TimeSlot}>{slots.Date.substr(0,10)} @ {slots.Start}:00 MST</option>
                                ) 
                                )}
                            </Form.Select>
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="formBasicEmail">
                            <Form.Label>Number of Participants: </Form.Label>
                            <Form.Select type="email" placeholder="Email Address"   required>
                                <option>Select Number of Participants</option>
                                <option value="1" onChange={(event) => total(event.target.value * excursionList[excursionIndex].Cost)}>1</option>
                                <option value="2" onChange={(event) => total(event.target.value * excursionList[excursionIndex].Cost)}>2</option>
                                <option value="3" onChange={(event) => total(event.target.value * excursionList[excursionIndex].Cost)}>3</option>
                                <option value="4" onChange={(event) => total(event.target.value * excursionList[excursionIndex].Cost)}>4</option>
                                <option value="5" onChange={(event) => total(event.target.value * excursionList[excursionIndex].Cost)}>5</option>
                                <option value="6" onChange={(event) => total(event.target.value * excursionList[excursionIndex].Cost)}>6</option>
                                <option value="7" onChange={(event) => total(event.target.value * excursionList[excursionIndex].Cost)}>7</option>
                                <option value="8" onChange={(event) => total(event.target.value * excursionList[excursionIndex].Cost)}>8</option>
                                <option value="9" onChange={(event) => total(event.target.value * excursionList[excursionIndex].Cost)}>9</option>
                                <option value="10" onChange={(event) => total(event.target.value * excursionList[excursionIndex].Cost)}>10</option>
                            </Form.Select>
                        </Form.Group>
                    </Form>

                        <h4>Cost: $Hardcode.00</h4>
                    </div>
                    </div>
                </div>

            <div className='cartFooter'>
            <form>
                <label>
                    I understand the risk associated with the above activities. <br/><br/>I understand the terms and conditions of service: <br/><br/>

                    <input size="40" type="text" name="name" placeholder="Signature" required />
                </label>
                <Link id="bookingConfButton" to="/bookingConf" disabled>
                    <Button type="submit" text="Complete Order" buttonType="completeButton" buttonStyle="completeStyle" disabled></Button>
                </Link>
            </form>
            <div>
                <h4>Subtotal: ${total}.00</h4>

            </div>
            </div>
        </div>
    );
  }

  export default Cart
