import  Axios  from "axios"
import {useEffect, useState} from "react"
import "./CustomerDash.css"
import { excursionDetails } from "../../components/excursionDetails"

const CustomerDash = () => {
    const [upcomingExc, setUpcomingExc] = useState([])
    const [customerInfo, setCustomerInfo] = useState([])
    const Username = 'alexisstew'           //hardcoded for now - get from local storage

    useEffect(()=> {
        Axios.get(`http://localhost:3001/customer/${Username}`)
        .then((response) => {
            setCustomerInfo(response.data);
        });
    }, [])

    useEffect(() => {
        Axios.get(`http://localhost:3001/customerExcursions/${Username}`)
        .then((response) => {
            setUpcomingExc(response.data);
        });
    }, []);

    return (
        <div className="customerDash">
            <div className="dashHead">
                {customerInfo.map((val) => {
                    return(
                        <h2>Welcome {val.FirstName} !</h2>
                    );
                })}
            </div>

            <h3 className="next">Upcoming Excursions: </h3>
            {upcomingExc.map((val,key) => {
                return (
                    <div className="upcoming">
                        <div className="upcomingImg">
                        </div>  
                        <div className="bookingInfo">
                            <div>
                                <h5>Booking ID: {val.BookingID}</h5>
                                <h5>Excursion Name: {val.ExcursionName}</h5>
                                <h5>Total Number of Participants: {val.Participants}</h5>
                                <h5>Total Cost: ${val.Cost}.00</h5>
                            </div>
                            <div>
                                <h5>Number of Adults: {val.NumAdults}</h5>
                                <h5>Number of Minors: {val.NumMinors}</h5>
                                <h5>Date: {val.Date.substr(0, 10)}</h5>
                                <h5>Time: {val.Start}:00 MST</h5>
                            </div>

                        </div>
                    </div>                  
                );
            })}
        </div>
    )
  }

  export default CustomerDash
