const ExcursionInfo = ({ excursion }) => {
    return (
      <div style={{ 
        backgroundImage: "url('" + excursion.imgUrl + "')"
      }} className='excursionInfo'>
                <div className='excursionTitle'>
                    <h2>{excursion.title}</h2>
                    <h4>Cost: ${excursion.cost}</h4>
                    <h4>Duration: {excursion.imgUrl}</h4>
                </div>
                <div className='excursionDescription'>
                    <p>{excursion.description}</p>
                </div>
                <div className='moreButton'>
                    <button>Learn More</button>
                </div>

      </div>
    )
  }

  export default ExcursionInfo
